	void AtomExpression(out Expr e) {
		Contract.Ensures(Contract.ValueAtReturn(out e) != null); IToken x; int n; BigNum bn; BigDec bd; BigFloat bf;
		List<Expr> es;  List<Variable> ds;  Trigger trig;
		List<TypeVariable> typeParams;
		IdentifierExpr id;
		QKeyValue kv;
